mapper.update:
UPDATE trips
SET content = #{tripsDto.content},
    sido_code = #{tripsDto.sidoCode},
    start_date = #{tripsDto.startDate},
    end_date = #{tripsDto.endDate}
WHERE trip_id = #{tripId}

UPDATE trips
SET copied_count = copied_count + 1
WHERE trip_id = #{tripId}

mapper.select:
SELECT is_public
from trips
where trip_id = #{tripId}
